import './App.css';
import React, { useState } from "react";
import { Button } from '@material-ui/core';
function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);
  console.log(input);
  console.log(messages);

  const sendMessage = (event) => {
    event.preventDefault();
    setMessages([...messages, input]);
    setInput("");

  }
  return (
    <div className="App">
      <h1>Hello clever Progremmer : rockr</h1>
      <form>
        <input key={input.id} value={input} onChange={event => setInput(event.target.value)}></input>
        <Button variant="contained" type="submit" color='primary' onClick={sendMessage}>Send message</Button>
      </form>
      {
        messages.map(message => (
          <p>{message}</p>
        ))
      }
    </div >
  );
}

export default App;
