/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
import './App.css';
import React, { useState, useEffect } from "react";
import { Button, FormControl, InputLabel, Input } from '@material-ui/core';
import Message from './Message';
function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([
    { username: "rafet", text: "merhaba" },
    { username: "melo", text: "selam" },
    { username: "turan", text: "naber" }
  ]);
  const [userName, setUserName] = useState("");


  const sendMessage = (event) => {
    event.preventDefault();
    setMessages([...messages, { username: userName, text: input }]);
    setInput("");

  }
  useEffect(() => {
    setUserName(prompt("ad覺n覺 gir lan"));
  }, []);
  return (
    <div>
      {
        !setMessages ? "" : (
          <div className="App" >
            <h1>Hello clever Progremmer : rockr</h1>
            <h2>welcome {userName}</h2>
            <form>
              <FormControl>
                <InputLabel  >Enter a message ...</InputLabel>
                <Input value={input} onChange={event => setInput(event.target.value)} />
                <Button disabled={!input} variant="contained" type="submit" color="secondary" onClick={sendMessage}>{!input ? "enter message" : "send message"}</Button>

              </FormControl>
            </form>
            {
              messages.map(message => (
                <Message user={message.username} texter={message.text} />

              ))
            }
          </div >
        )
      }
    </div>
  );
}

export default App;
